var               
  bind = function(fn, me){ return function(){ return fn.apply(me, arguments); }; },
  extend = function(child, parent) { for (var key in parent) { if (hasProp.call(parent, key)) child[key] = parent[key]; } function ctor() { this.constructor = child; } ctor.prototype = parent.prototype; child.prototype = new ctor(); child.__super__ = parent.prototype; return child; },
  hasProp = {}.hasOwnProperty;

if (Meteor.isClient) {
  if (!Function.prototype.bind) {
    Function.prototype.bind = function(oThis) {
      var aArgs, fBound, fNOP, fToBind;
      if (typeof this !== "function") {
        throw new TypeError("Function.prototype.bind - what is trying to be bound is not callable");
      }
      aArgs = Array.prototype.slice.call(arguments, 1);
      fToBind = this;
      fNOP = function() {};
      fBound = function() {
        var func;
        func = (this instanceof fNOP && oThis) ? this : oThis;
        return fToBind.apply(func, aArgs.concat(Array.prototype.slice.call(arguments)));
      };
      fNOP.prototype = this.prototype;
      fBound.prototype = new fNOP();
      return fBound;
    };
  }
  JobCollection = (function(superClass) {
    extend(JobCollection, superClass);

    function JobCollection(root, options) {
      if (root == null) {
        root = 'queue';
      }
      if (options == null) {
        options = {};
      }
      this._toLog = bind(this._toLog, this);
      if (!(this instanceof JobCollection)) {
        return new JobCollection(root, options);
      }
      JobCollection.__super__.constructor.call(this, root, options);
      this.logConsole = false;
      this.isSimulation = true;
      if (options.connection == null) {
        Meteor.methods(this._generateMethods());
      } else {
        options.connection.methods(this._generateMethods());
      }
    }

    JobCollection.prototype._toLog = function(userId, method, message) {
      if (this.logConsole) {
        return console.log((new Date()) + ", " + userId + ", " + method + ", " + message + "\n");
      }
    };

    return JobCollection;

  })(share.JobCollectionBase);
}
