import { Tasks } from '../../';
import { TASK_QUERIES, TASK_STATUS } from '../tasksConstants';

export default Tasks.createQuery(TASK_QUERIES.LOAN_TASKS_LIST, {
  $filters: {
    status: { $in: [TASK_STATUS.ACTIVE, TASK_STATUS.COMPLETED] },
  },
  //   $filter({ filters, options, params }) {
  //     console.log(params);
  //     filters.status = { $in: [TASK_STATUS.ACTIVE, TASK_STATUS.COMPLETED] };

  //     // if (params.loanId) {
  //     //   filters.loanId = params.loanId;
  //     // }
  //   },
  $postFilter(results, params) {
    console.log(results);
    console.log(params);
    return results.filter((r) => {
      if (
        (r.loan && r.loan._id === params.loanId) ||
        (r.property && r.propertyId === params.propertyId)
      ) {
        return r;
      }
    });
  },
  $options: {
    sort: {
      createdAt: -1,
    },
  },
  $paginate: true,
  status: 1,
  type: 1,
  createdAt: 1,
  updatedAt: 1,
  dueAt: 1,
  assignedUser: {
    emails: 1,
    roles: 1,
    username: 1,
  },
  user: {
    emails: 1,
    username: 1,
  },
  borrower: {
    user: {
      assignedTo: 1,
    },
  },
  loan: {
    user: {
      assignedTo: 1,
    },
  },
  property: {
    user: {
      assignedTo: 1,
    },
  },
  userId: 1,
});
