import PropTypes from 'prop-types';
import React from 'react';
import { Meteor } from 'meteor/meteor';
import classnames from 'classnames';
import { Link } from 'react-router-dom';

import Button from 'core/components/Button';
import Icon from 'core/components/Icon';

import SideNavList from './SideNavList.jsx';

const adminLinks = [
  {
    link: '/',
    icon: <span className="icon fa fa-home fa-2x" />,
    label: 'Home',
  },
  {
    link: '/users',
    icon: <span className="icon fa fa-users fa-2x" />,
    label: 'Utilisateurs',
  },
  {
    link: '/loans',
    icon: <span className="icon fa fa-files-o fa-2x" />,
    label: 'Demandes de prÃªt',
  },
  {
    link: '/dev',
    icon: <span className="icon fa fa-code fa-2x" />,
    label: 'Dev',
  },
];

const partnerLinks = [
  {
    link: '/partner',
    icon: <span className="icon fa fa-home fa-2x" />,
    label: 'Home',
  },
];

const SideNav = ({ type, history }) => {
  let links = [];

  switch (type) {
  case 'admin':
    links = adminLinks;
    break;
  case 'partner':
    links = partnerLinks;
    break;
  default:
    break;
  }

  // never hide sidenav for admins
  const classes = classnames({
    'side-nav': true,
    'hidden-xs': type !== 'admin',
  });

  return (
    <nav className={classes}>
      <Link to="/" className="logo">
        <img src="/img/logo_black.svg" alt="e-Potek" />
      </Link>

      <SideNavList links={links} />

      <div className="logout text-center">
        <Button
          label="DÃ©connexion"
          onClick={() => Meteor.logout(() => history.push('/login'))}
          icon={<Icon type="powerOff" />}
        />
      </div>
    </nav>
  );
};

SideNav.propTypes = {
  type: PropTypes.string.isRequired,
};

export default SideNav;
