import { Tasks } from '../../';
import { TASK_QUERY, TASK_STATUS } from '../tasksConstants';

export default Tasks.createQuery(TASK_QUERY.LOAN_TASKS_LIST, {
  $filter({ filters, options, params }) {
    filters.status = { $in: [TASK_STATUS.ACTIVE, TASK_STATUS.COMPLETED] };

    if (params.loanId) {
      filters.loanId = params.loanId;
    }
  },
  $options: {
    sort: {
      createdAt: -1,
    },
  },
  $paginate: true,
  status: 1,
  type: 1,
  createdAt: 1,
  updatedAt: 1,
  dueAt: 1,
  assignedUser: {
    emails: 1,
    roles: 1,
    username: 1,
  },
  user: {
    emails: 1,
    username: 1,
  },
  borrower: {
    user: {
      assignedTo: 1,
    },
  },
  loan: {
    user: {
      assignedTo: 1,
    },
  },
  property: {
    user: {
      assignedTo: 1,
    },
  },
  userId: 1,
});
