export const getWidth = () => {
  if (global.window) {
    const w = window;
    const d = document;
    const documentElement = d.documentElement;
    const body = d.getElementsByTagName('body')[0];
    return w.innerWidth || documentElement.clientWidth || body.clientWidth;
  }
};

const getSubdomain = () => {
  if (global.window) {
    const fullPath = window.location.host;
    // window.location.host is subdomain.domain.com
    return fullPath.split('.')[0];
  }
};

export const isDemo = () => getSubdomain() === 'demo';
export const isWww = () => getSubdomain() === 'www';
export const isApp = () => getSubdomain() === 'app';
export const isAdmin = () => getSubdomain() === 'admin';
export const isLender = () => getSubdomain() === 'lender';

export const storageAvailable = (type) => {
  try {
    const storage = window[type];
    const x = '__storage_test__';
    storage.setItem(x, x);
    storage.removeItem(x);
    return true;
  } catch (e) {
    return false;
  }
};

export const easeOut = (min, max, intervals) => {
  const diff = 1 / intervals;
  const difference = max - min;
  const curve = [];

  for (let i = diff; i <= 1; i += diff) {
    curve.push(min + difference * Math.pow(i, 0.48));
  }
  return curve;
};
