import { Meteor } from 'meteor/meteor';
import React from 'react';
import DropdownMenu from 'core/components/DropdownMenu/';
import { callMutation, mutations } from 'core/api';
import { TASK_STATUS } from 'core/api/tasks/tasksConstants';

const changeAssignedUser = ({
  user,
  taskId,
  taskUserId,
  initialAssignment,
}) => {
  if (initialAssignment && taskUserId) {
    callMutation(mutations.ASSIGN_ADMIN_TO_USER, {
      userId: taskUserId,
      adminId: user._id,
    }).then(callMutation(mutations.TASK_CHANGE_STATUS, {
      taskId,
      newStatus: TASK_STATUS.COMPLETED,
    }));
  } else {
    callMutation(mutations.TASK_CHANGE_USER, {
      taskId,
      newUser: user._id,
    });
  }
};

const getMenuItems = (
  users,
  taskAssignedTo,
  taskId,
  taskUserId,
  initialAssignment,
) => {
  const options = users.map(user => ({
    id: user._id,
    show: user._id !== taskAssignedTo, // role
    label: user.emails[0].address,
    link: false,
    onClick: () => {
      changeAssignedUser({ user, taskId, taskUserId, initialAssignment });
    },
  }));
  return options;
};

const TasksAssignedUserDropdown = (props) => {
  // const { data, isLoading, error, taskId, taskUser, styles } = props;
  const {
    data,
    isLoading,
    error,
    taskId,
    taskAssignedTo,
    taskUserId,
    initialAssignment,
    styles,
  } = props;

  if (isLoading) {
    return null;
  }
  if (error) {
    return <div>Error: {error.reason}</div>;
  }

  return (
    <DropdownMenu
      iconType="personAdd"
      options={getMenuItems(
        data,
        taskAssignedTo,
        taskId,
        taskUserId,
        initialAssignment,
      )}
      style={styles}
    />
  );
};

export default TasksAssignedUserDropdown;
