import { Tasks } from '../../';
import { TASK_QUERIES } from '../tasksConstants';

export default Tasks.createQuery(TASK_QUERIES.TASKS, {
  $filter({ filters, options, params }) {
    if (params.unassigned){
      filters.assignedTo = undefined;
    } else{
      if (params.userId) {
        filters.assignedTo = params.userId;
      }
    }

  },
  $options: {
    sort: {
      createdAt: -1,
    },
  },
  $paginate: true,
  status: 1,
  type: 1,
  createdAt: 1,
  updatedAt: 1,
  dueAt: 1,
  assignedUser: {
    emails: 1,
    roles: 1,
    username: 1,
  },
  user: {
    emails: 1,
    username: 1,
  },
  borrower: {
    user: {
      assignedTo: 1,
    },
  },
  loan: {
    user: {
      assignedTo: 1,
    },
  },
  property: {
    user: {
      assignedTo: 1,
    },
  },
  userId: 1,
});
